import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [Peoples, setPeoples] = useState([]);

  async function SearchPeople() {
    const response = await axios.get('https://swapi.co/api/people/');
    setPeoples(response.data.results)
  }


  useEffect(() => {
    SearchPeople()
  }, [])

  function DeletePeople(url) {
    var Peoplesx = Peoples.filter(function (people) {
      return people.url !== url;
    });
    setPeoples(Peoplesx);
  }

  return (
    <div className="App">
      <div className="HeaderSelect">
        <label>Clique sobre o nome para excluir *: </label><br />
        <select name="SelectPeoples" onChange={e => DeletePeople(e.target.value)}>
          <option>Selecione um Item</option>
          {Peoples.map(P => (
            <option value={P.url} key={P.url}>{P.name}</option>
          ))}
        </select>
        <button>Excluir</button>
      </div>

      <ul className="list-peoples">
        {Peoples.map(P => (
          <li key={P.url} className="Header">
            <h3 style={{ 'color': P.eye_color, textDecoration: 'underline' }}>{P.name}</h3>
            <h4>Olhos: {P.hair_color}</h4>
            <h4>Nasc: {P.birth_year}</h4>
            <h5>GenÃªro: {P.gender}</h5>
            <button onClick={() => (
              DeletePeople(P.url)
            )}>Excluir</button>
          </li>
        ))}

      </ul>
    </div >
  );
}

export default App;
